import React, { useState } from 'react';
import { bookSlot } from '../services/apiService';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const BookingForm = ({ horario, modality, onBookingSuccess }) => {
  const [studentName, setStudentName] = useState('');
  const [book, setBook] = useState('');
  const [content, setContent] = useState('');
  const [whatsapp, setWhatsapp] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookingData = {
      horario,
      modality,
      studentName,
      book,
      content,
      whatsapp,
    };
    try {
      await bookSlot(bookingData); // Chama a API para agendar
      toast.success('Agendamento realizado com sucesso!'); // Notificação de sucesso
      setStudentName('');
      setBook('');
      setContent('');
      setWhatsapp('');
      onBookingSuccess(); // Atualiza a lista de horários
    } catch (error) {
      toast.error('Erro ao realizar o agendamento'); // Notificação de erro
    }
  };

  return (
    <div>
      <h3>Agendar: {horario} ({modality})</h3>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome do aluno"
          value={studentName}
          onChange={(e) => setStudentName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Livro"
          value={book}
          onChange={(e) => setBook(e.target.value)}
        />
        <input
          type="text"
          placeholder="Conteúdo"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <input
          type="text"
          placeholder="WhatsApp"
          value={whatsapp}
          onChange={(e) => setWhatsapp(e.target.value)}
        />
        <button type="submit">Agendar</button>
      </form>
      <ToastContainer /> {/* Container para as notificações */}
    </div>
  );
};

export default BookingForm;
