import React, { useState } from 'react';
import { bookSlot } from '../services/apiService';

const BookingForm = ({ horario, modality, onBookingSuccess }) => {
  const [studentName, setStudentName] = useState('');
  const [book, setBook] = useState('');
  const [content, setContent] = useState('');
  const [whatsapp, setWhatsapp] = useState('');
  const [notification, setNotification] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const bookingData = {
      horario,
      modality,
      studentName,
      book,
      content,
      whatsapp,
    };
    try {
      await bookSlot(bookingData);
      setNotification({ message: 'Agendamento realizado com sucesso!', type: 'success' });
      setStudentName('');
      setBook('');
      setContent('');
      setWhatsapp('');
      onBookingSuccess();
    } catch (error) {
      setNotification({ message: 'Erro ao realizar o agendamento', type: 'error' });
    }
  };

  return (
    <div>
      <h3>Agendar: {horario} ({modality})</h3>
      {notification && <p>{notification.message}</p>}
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nome do aluno"
          value={studentName}
          onChange={(e) => setStudentName(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Livro"
          value={book}
          onChange={(e) => setBook(e.target.value)}
        />
        <input
          type="text"
          placeholder="ConteÃºdo"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <input
          type="text"
          placeholder="WhatsApp"
          value={whatsapp}
          onChange={(e) => setWhatsapp(e.target.value)}
        />
        <button type="submit">Agendar</button>
      </form>
    </div>
  );
};

export default BookingForm;
