import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/bonus-class',
});

const fetchAvailableSlots = (modality) => {
  return api.get('/available', { params: { modality } });
};

const submitBooking = (bookingData) => {
  return api.post('/book', bookingData);
};

export default {
  fetchAvailableSlots,
  submitBooking,
};
