import React, { useState } from 'react';
import apiService from '../services/apiService';

function BookingForm({ slot, onSubmit }) {
  const [name, setName] = useState('');
  const [book, setBook] = useState('');
  const [content, setContent] = useState('');
  const [whatsapp, setWhatsapp] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    const bookingData = {
      name,
      book,
      content,
      modality: slot[2] === 'O' ? 'online' : slot[2] === 'P' ? 'presencial' : slot[2],
      whatsapp,
    };
    try {
      await apiService.submitBooking(bookingData);
      onSubmit(bookingData);
    } catch (error) {
      console.error('Erro ao realizar agendamento:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Agendar Aula</h2>
      <label>
        Nome:
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
      </label>
      <label>
        Livro:
        <input type="text" value={book} onChange={(e) => setBook(e.target.value)} required />
      </label>
      <label>
        Conte√∫do:
        <input type="text" value={content} onChange={(e) => setContent(e.target.value)} required />
      </label>
      <label>
        WhatsApp:
        <input type="text" value={whatsapp} onChange={(e) => setWhatsapp(e.target.value)} required />
      </label>
      <button type="submit">Agendar</button>
    </form>
  );
}

export default BookingForm;
