import React, { useState } from 'react';
import { bookSlot } from '../services/apiService';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const BookingForm = ({ horario, modality, onBookingSuccess }) => {
  const [step, setStep] = useState(1);
  const [studentName, setStudentName] = useState('');
  const [book, setBook] = useState('');
  const [content, setContent] = useState('');
  const [whatsapp, setWhatsapp] = useState('');

  const handleNext = () => {
    if (step === 1 && studentName) {
      setStep(2);
    } else if (step === 2 && book && content && whatsapp) {
      handleSubmit();
    } else {
      toast.error('Por favor, preencha todos os campos');
    }
  };

  const handleSubmit = async () => {
    const bookingData = {
      horario,
      modality,
      studentName,
      book,
      content,
      whatsapp,
    };
    try {
      await bookSlot(bookingData);
      toast.success('Agendamento realizado com sucesso!');
      onBookingSuccess();
    } catch (error) {
      toast.error('Erro ao realizar o agendamento');
    }
  };

  return (
    <div className="booking-form">
      <h3>Agendar: {horario} ({modality})</h3>
      {step === 1 && (
        <div className="form-step">
          <label>Nome do Aluno</label>
          <input
            type="text"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
            required
          />
          <button type="button" onClick={handleNext}>Próximo</button>
        </div>
      )}
      {step === 2 && (
        <div className="form-step">
          <label>Book</label>
          <input
            type="text"
            value={book}
            onChange={(e) => setBook(e.target.value)}
            required
          />
          <label>Conteúdo</label>
          <input
            type="text"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          />
          <label>WhatsApp</label>
          <input
            type="tel"
            value={whatsapp}
            onChange={(e) => setWhatsapp(e.target.value)}
            required
          />
          <button type="button" onClick={handleNext}>Agendar</button>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default BookingForm;
