import React, { useState, useEffect } from 'react';
import { getAvailableSlots, bookSlot } from '../services/apiService';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const BookingForm = ({ modality, step, setStep, onBack }) => {
  const [slots, setSlots] = useState([]);
  const [selectedHorario, setSelectedHorario] = useState('');
  const [studentName, setStudentName] = useState('');
  const [book, setBook] = useState('');
  const [content, setContent] = useState('');
  const [whatsapp, setWhatsapp] = useState('');

  useEffect(() => {
    const fetchSlots = async () => {
      try {
        const response = await getAvailableSlots(modality);
        setSlots(response.data);
      } catch (error) {
        toast.error('Erro ao carregar horários');
      }
    };
    if (modality) fetchSlots();
  }, [modality]);

  const handleNext = () => {
    if (step === 2 && !selectedHorario) {
      toast.error('Por favor, selecione um horário');
      return;
    }
    if (step === 3 && !studentName) {
      toast.error('Por favor, preencha o nome');
      return;
    }
    if (step === 4 && !book) {
      toast.error('Por favor, preencha o livro');
      return;
    }
    if (step === 5 && !content) {
      toast.error('Por favor, preencha o conteúdo');
      return;
    }
    if (step === 6 && !whatsapp) {
      toast.error('Por favor, preencha o WhatsApp');
      return;
    }
    if (step < 7) {
      setStep(step + 1);
    }
  };

  const handleBack = () => {
    if (step > 2) {
      setStep(step - 1);
    } else {
      onBack();
    }
  };

  const handleSubmit = async () => {
    const bookingData = {
      horario: selectedHorario,
      modality,
      studentName,
      book,
      content,
      whatsapp,
    };
    try {
      await bookSlot(bookingData);
      toast.success('Agendamento realizado com sucesso!');
      setStep(8); // Etapa de confirmação
    } catch (error) {
      toast.error('Erro ao realizar o agendamento');
    }
  };

  return (
    <div className="booking-form">
      {step === 2 && (
        <div className={`step ${step === 2 ? 'step-enter' : 'step-exit'}`}>
          <label>Horário:</label>
          <select
            value={selectedHorario}
            onChange={(e) => setSelectedHorario(e.target.value)}
          >
            <option value="" disabled>Escolha um horário</option>
            {slots.map((horario, index) => (
              <option key={index} value={horario}>
                {horario}
              </option>
            ))}
          </select>
        </div>
      )}
      {step === 3 && (
        <div className={`step ${step === 3 ? 'step-enter' : 'step-exit'}`}>
          <label>Nome do Aluno:</label>
          <input
            type="text"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
          />
        </div>
      )}
      {step === 4 && (
        <div className={`step ${step === 4 ? 'step-enter' : 'step-exit'}`}>
          <label>Livro:</label>
          <input
            type="text"
            value={book}
            onChange={(e) => setBook(e.target.value)}
          />
        </div>
      )}
      {step === 5 && (
        <div className={`step ${step === 5 ? 'step-enter' : 'step-exit'}`}>
          <label>Conteúdo:</label>
          <input
            type="text"
            value={content}
            onChange={(e) => setContent(e.target.value)}
          />
        </div>
      )}
      {step === 6 && (
        <div className={`step ${step === 6 ? 'step-enter' : 'step-exit'}`}>
          <label>WhatsApp:</label>
          <input
            type="tel"
            value={whatsapp}
            onChange={(e) => setWhatsapp(e.target.value)}
          />
        </div>
      )}
      {step === 7 && (
        <div className={`step ${step === 7 ? 'step-enter' : 'step-exit'}`}>
          <h3>Confirmação</h3>
          <p>Horário: {selectedHorario}</p>
          <p>Modalidade: {modality}</p>
          <p>Nome: {studentName}</p>
          <p>Livro: {book}</p>
          <p>Conteúdo: {content}</p>
          <p>WhatsApp: {whatsapp}</p>
          <button onClick={handleSubmit}>Agendar</button>
        </div>
      )}
      {step === 8 && (
        <div className={`step ${step === 8 ? 'step-enter' : 'step-exit'}`}>
          <span className="check">✔</span>
          <p>Agendado com sucesso!</p>
        </div>
      )}
      {step < 7 && (
        <div className="navigation-buttons">
          <button onClick={handleBack} className="nav-button">←</button>
          <button onClick={handleNext} className="nav-button">→</button>
        </div>
      )}
      <ToastContainer />
    </div>
  );
};

export default BookingForm;
